//This example ASP.NET wrapper code will trigger a refresh.

using System;
using System.Collections.Generic;
using System.IO;
using System.Net;
using System.Runtime.Serialization.Json;
using System.Text;
using System.Threading.Tasks;

//please add the above library references to your porject if they are not included yet.

namespace API
{
    public class RefreshCall
    {
        /// <summary>
        /// this is an example to refresh applications via API call (c#)
        /// </summary>
        public static async Task<RootObject> Refresh()
        {
            RootObject result = null;

            // credit sense api request url
            string api_url = "https://api.creditsense.com.au";

            // your credential information here
            string api_key = "XXXXXXXX-XXXX-XXXX-XXXX-XXXXXXXXXXXX";
            string api_token = "XXXXXXXX-XXXX-XXXX-XXXX-XXXXXXXXXXXX";

            //api url and post body/parameters
            string URL = api_url + "/v2/" + api_key + "/refresh";
            string postBody = "{\"Settings\" : {\"API_Token\":\"" + api_token + "\"},\"Payload\" : {\"Apps\" :[";
            postBody += "{\"App_ID\":\"1234567\", \"New_App_Ref\":\"xxxxxxxxxx\", \"Is_Priority\":\"True\"},";    // application to be refresh
            postBody += "{\"App_ID\":\"12345678\", \"New_App_Ref\":\"xxxxxxxxxx\", \"Is_Priority\":\"True\"}";   // application to be refresh
            postBody += "]}}";

            HttpWebRequest apirptreq = (HttpWebRequest)WebRequest.Create(URL);
            apirptreq.ContentType = "application/json";
            apirptreq.Method = "POST";

            byte[] postString = Encoding.UTF8.GetBytes(postBody);

            apirptreq.ContentLength = postString.Length;
            Stream jsonStream = apirptreq.GetRequestStream();

            jsonStream.Write(postString, 0, postString.Length);
            jsonStream.Close();

            // get response
            HttpWebResponse apirsp;
            try
            {
                // http status with 200
                apirsp = (HttpWebResponse)apirptreq.GetResponse();
            }
            catch (WebException ex)
            {
                apirsp = (HttpWebResponse)(ex.Response);
            }

            using (Stream jsonRspStream = apirsp.GetResponseStream())
            {
                string responseStr = null;

                using (StreamReader reader = new StreamReader(jsonRspStream))
                {

                    responseStr = reader.ReadToEnd();

                    var serializer = new DataContractJsonSerializer(typeof(RootObject));
                    var ms = new MemoryStream(Encoding.UTF8.GetBytes(responseStr));
                    //get the return json object if reaching this point
                    var data = (RootObject)serializer.ReadObject(ms);

                    // you can work throught the result here
                    if (data != null && data.Response != null && data.Response.Count > 0 && data.Error_Response == null) {
                        //refresh successfully
                        System.Diagnostics.Debug.WriteLine("OK");
                    }else{
                        // check applications with success status
                        if (data != null && data.Response != null && data.Response.Count > 0) {
                            foreach (AppSuccess app in data.Response)
                            {
                                //you can access application data here,
                                System.Diagnostics.Debug.WriteLine("success:"+app.App_ID);
                            }
                        }

                        // check applications with error status
                        if (data != null && data.Error_Response != null)
                        {
                            Console.Write(data.Error_Response.Error_Code);
                            if (data.Error_Response.Apps != null && data.Error_Response.Apps.Count > 0)
                            {
                                foreach (AppFail app in data.Error_Response.Apps)
                                {
                                    //you can access fail application data here,
                                    System.Diagnostics.Debug.WriteLine("fail:" + app.App_ID);
                                }
                            }
                        }

                        // log the error on your end so that you have visibility
                        System.Diagnostics.Debug.WriteLine(data);
                    }
                }
            }

            return result;
        }

        // the below classes are generate to match the api return json

        /// <summary>
        /// return resonse object
        /// </summary>
        public class AppSuccess
        {
            public int App_ID { get; set; }
            public string App_Ref { get; set; }
            public bool Is_Priority { get; set; }
            public bool Is_New_App { get; set; }
            public int Existing_App_ID { get; set; }
        }

        /// <summary>
        /// return application object
        /// </summary>
        public class AppFail
        {
            public int App_ID { get; set; }
            public int Error_Code { get; set; }
            public string Error_Type { get; set; }
            public string Error_Description { get; set; }
        }

        /// <summary>
        /// return error resonse object
        /// </summary>
        public class ErrorResponse
        {
            public int Error_Code { get; set; }
            public string Error_Type { get; set; }
            public string Error_Description { get; set; }
            public List<AppFail> Apps { get; set; }
        }

        /// <summary>
        /// return json object - root level
        /// </summary>
        public class RootObject
        {
            public List<AppSuccess> Response { get; set; }
            public ErrorResponse Error_Response { get; set; }
        }
    }



}

//This example PHP wrapper code will trigger a refresh.

public function OnRefresh(){
    try {
        // credit sense api request url
        $apiUrl = 'https://api.creditsense.com.au';

        // credit sense api details (replace with your details, listed at ...)
        $apiKey = 'XXXXXXXX-XXXX-XXXX-XXXX-XXXXXXXXXXXX';
        $apiToken = 'XXXXXXXX-XXXX-XXXX-XXXX-XXXXXXXXXXXX';

        // set up the application details to be refreshed
        $apps = array();

        // first application information
        $app1 = array('App_ID' => "1234567", 'New_App_Ref' => "xxxxxxxxxxxx", 'Is_Priority' => "True");
        array_push($apps, $app1);

        // repeat the first example to set up multiple applications if this is required
        $app2 = array('App_ID' => "12345678");
        array_push($apps, $app2);

        // do the refresh api call to refresh the applications
        $postFields = array(
            'Settings' => array('API_Token' => $apiToken),
            'Payload' => array('Apps' => $apps)
        );

        // curl action
        $curl = curl_init($apiUrl . '/v2/' . $apiKey . '/refresh');
        curl_setopt($curl, CURLOPT_POST, true);
        curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($curl, CURLOPT_POSTFIELDS, json_encode($postFields));

        $result = curl_exec($curl);
        curl_close($curl);

        $result = json_decode($result, true);
        print_r($result);

        // refresh successfully
        if ($result && $result['Response'] && count($result['Response']) > 0 && !$result['Error_Response']) {
            echo 'OK';
        } else {

            // check applications with success status
            if ($result && $result['Response'] && count($result['Response']) > 0){
                foreach ($result['Response'] as $app) {
                    // retrieve details from the response
                    $appId = $app["App_ID"];
                    $appRef = $app["App_Ref"];
                    $isPriority = $app["Is_Priority"];
                    $isNewApp = $app["Is_New_App"];
                    $existingAppId = $app["Existing_App_ID"];

                    echo "done: " . $appId . "<br>";
                }
            }

            // check applications with error status
            if ($result && $result['Error_Response']){
                // retrieve details from the response
                $errorCode = $result['Error_Response']["Error_Code"];
                $errorType = $result['Error_Response']["Error_Type"];
                $errorDescription = $result['Error_Response']["Error_Description"];
                echo "error: " . $errorCode . "<br>";

                // get applications which is failed to refresh
                if($result['Error_Response']['Apps'] && count($result['Error_Response']['Apps']) > 0){
                    foreach ($result['Error_Response']['Apps'] as $app) {
                        // retrieve details from the response
                        $appId = $app["App_ID"];

                        echo "failed: " . $appId . "<br>";
                    }
                }

            }

            // log the error on your end so that you have visibility
            $this->logFailedApiCall($result);

        }

    }catch(\Exception $e){
        // log the error on your end so that you have visibility
        $this->logFailedRefresh($e);

        echo $e->getMessage();
    }
}

